int conntrack_flagstr ( struct conn_object * conn , char * pstr , int len ) { if ( pstr == NULL || conn == NULL ) { return - E_INVALID ; } if ( conn -> flags & CONN_MODIFIED ) { strncpy ( pstr , "M" , len - 1 ) ; } if ( conn -> flags & CONN_INJECTED ) { strncpy ( pstr , "I" , len - 1 ) ; } if ( conn -> DISSECTOR . user ) { strncpy ( pstr , "*" , len - 1 ) ; } return E_SUCCESS ; } 