int arch_uprobe_exception_notify ( struct notifier_block * self , unsigned long val , void * data ) { struct die_args * args = data ; struct pt_regs * regs = args -> regs ; if ( ! user_mode ( regs ) ) { return NOTIFY_DONE ; } switch ( val ) { case DIE_BPT : if ( uprobe_pre_sstep_notifier ( regs ) ) { return NOTIFY_STOP ; } break ; case DIE_SSTEP : if ( uprobe_post_sstep_notifier ( regs ) ) { return NOTIFY_STOP ; } default : break ; } return NOTIFY_DONE ; } 