static int davinci_rtc_read_time ( struct device * dev , struct rtc_time * tm ) { struct davinci_rtc * davinci_rtc = dev_get_drvdata ( dev ) ; u16 days = 0 ; u8 day0 , day1 ; unsigned long flags ; spin_lock_irqsave ( & davinci_rtc_lock , flags ) ; davinci_rtcss_calendar_wait ( davinci_rtc , NULL ) ; tm -> tm_sec = bcd2bin ( rtcss_read ( davinci_rtc , PRTCSS_RTC_SEC ) ) ; davinci_rtcss_calendar_wait ( davinci_rtc ) ; tm -> tm_min = bcd2bin ( rtcss_read ( davinci_rtc , PRTCSS_RTC_MIN ) ) ; davinci_rtcss_calendar_wait ( davinci_rtc ) ; tm -> tm_hour = bcd2bin ( rtcss_read ( davinci_rtc , PRTCSS_RTC_HOUR ) ) ; davinci_rtcss_calendar_wait ( davinci_rtc ) ; day0 = rtcss_read ( davinci_rtc , PRTCSS_RTC_DAY0 ) ; davinci_rtcss_calendar_wait ( davinci_rtc ) ; day1 = rtcss_read ( davinci_rtc , PRTCSS_RTC_DAY1 ) ; spin_unlock_irqrestore ( & davinci_rtc_lock , flags ) ; days |= day1 ; days <<= 8 ; days |= day0 ; if ( convertfromdays ( days , tm ) < 0 ) { return - EINVAL ; } return 0 ; } 