static void do_help ( void ) { printf ( "  -v    --version     Display version info and exit\n" ) ; printf ( "  -h    --help        Display this help and exit\n" ) ; printf ( "  -d D  --device=D    Use device D for audio output instead of default\n" ) ; printf ( "MIDI Options:\n" ) ; printf ( "  -n    --roundtempo  Round tempo to nearest whole number\n" ) ; printf ( "  -s    --skipsilentstart Skips any silence at the start of playback\n" ) ; printf ( "  -t    --test_midi   Listen to test MIDI\n" ) ; printf ( "Non-MIDI Options:\n" ) ; printf ( "  -x    --tomidi      Convert file to midi and save to file\n" ) ; printf ( "  -g    --convert     Convert XMI: 0 - No Conversion (default)\n" ) ; printf ( "                                   1 - MT32 to GM\n" ) ; printf ( "                                   2 - MT32 to GS\n" ) ; printf ( "  -f F  --frequency=F Use frequency F Hz for playback (MUS)\n" ) ; printf ( "Software Wavetable Options:\n" ) ; printf ( "  -o W  --wavout=W    Save output to W in 16bit stereo format wav file\n" ) ; printf ( "  -l    --log_vol     Use log volume adjustments\n" ) ; printf ( "  -r N  --rate=N      Set sample rate to N samples per second (Hz)\n" ) ; printf ( "  -c P  --config=P    Point to your wildmidi.cfg config file name/path\n" ) ; printf ( "                      defaults to: %s\n" , WILDMIDI_CFG ) ; printf ( "  -m V  --mastervol=V Set the master volume (0..127), default is 100\n" ) ; } 