static void ccid_bulk_in_copy_to_guest ( USBCCIDState * s , USBPacket * p , unsigned int max_packet_size ) { int len = 0 ; ccid_bulk_in_get ( s ) ; if ( s -> current_bulk_in != NULL ) { len = MIN ( s -> current_bulk_in -> len - s -> current_bulk_in -> pos , p -> iov . size ) ; if ( len ) { usb_packet_copy ( p , s -> current_bulk_in -> data + s -> current_bulk_in -> pos , len ) ; } s -> current_bulk_in -> pos += len ; if ( s -> current_bulk_in -> pos == s -> current_bulk_in -> len && len != max_packet_size ) { ccid_bulk_in_release ( s ) ; } } else { p -> status = USB_RET_NAK ; } if ( len ) { DPRINTF ( s , D_MORE_INFO , "%s: %zd/%d req/act to guest (BULK_IN)\n" , __func__ , p -> iov . size , len ) ; } } 