void snic_req_free ( struct snic * snic , struct snic_req_info * rqi ) { SNIC_BUG_ON ( rqi -> req == rqi -> abort_req ) ; SNIC_BUG_ON ( rqi -> req == rqi -> dr_req ) ; SNIC_BUG_ON ( rqi -> sge_va != 0 ) ; SNIC_SCSI_DBG ( snic -> shost , "Req_free:rqi %p:ioreq %p:abt %p:dr %p\n" , rqi , rqi -> req , rqi -> abort_req , rqi -> dr_req ) ; if ( rqi -> abort_req ) { if ( rqi -> abort_req -> req_pa ) { pci_unmap_single ( snic -> pdev , rqi -> abort_req -> req_pa , sizeof ( snic_host_req ) , PCI_DMA_TODEVICE ) ; } mempool_free ( rqi -> abort_req , snic -> req_pool [ SNIC_REQ_TM_CACHE ] ) ; } if ( rqi -> dr_req ) { mempool_free ( rqi -> dr_req , snic -> req_pool [ SNIC_REQ_TM_CACHE ] ) ; } if ( rqi -> req -> req_pa ) { pci_unmap_single ( snic -> pdev , rqi -> req -> req_pa , rqi -> req_len , PCI_DMA_TODEVICE ) ; } mempool_free ( rqi , snic -> req_pool [ rqi -> rq_pool_type ] ) ; } 