static char * xf_input_touch_state_string ( DWORD flags ) { if ( flags & RDPINPUT_CONTACT_FLAG_DOWN ) { return "RDPINPUT_CONTACT_FLAG_DOWN" ; } if ( flags & RDPINPUT_CONTACT_FLAG_UPDATE ) { return "RDPINPUT_CONTACT_FLAG_UPDATE" ; } if ( flags & RDPINPUT_CONTACT_FLAG_UP ) { return "RDPINPUT_CONTACT_FLAG_UP" ; } if ( flags & RDPINPUT_CONTACT_FLAG_INRANGE ) { return "RDPINPUT_CONTACT_FLAG_INRANGE" ; } if ( flags & RDPINPUT_CONTACT_FLAG_INCONTACT ) { return "RDPINPUT_CONTACT_FLAG_INCONTACT" ; } else { return "RDPINPUT_CONTACT_FLAG_UNKNOWN" ; } } 