void vnt_enable_power_saving ( struct vnt_private * priv , u16 listen_interval ) { u16 aid = priv -> current_aid | BIT ( 14 ) | BIT ( 15 ) ; if ( priv -> op_mode != NL80211_IFTYPE_ADHOC ) { vnt_mac_write_word ( priv , MAC_REG_AIDATIM , aid ) ; } vnt_mac_reg_bits_on ( priv , MAC_REG_PSCTL , PSCTL_PSEN ) ; vnt_mac_reg_bits_on ( priv , MAC_REG_PSCFG , PSCFG_AUTOSLEEP ) ; vnt_mac_reg_bits_on ( priv , MAC_REG_PSCTL , PSCTL_GO2DOZE ) ; if ( listen_interval >= 2 ) { vnt_mac_reg_bits_off ( priv , MAC_REG_PSCTL , PSCTL_ALBCN ) ; vnt_mac_reg_bits_on ( priv , MAC_REG_PSCTL , PSCTL_LNBCN ) ; } else { vnt_mac_reg_bits_on ( priv , MAC_REG_PSCTL , PSCTL_ALBCN ) ; } dev_dbg ( & priv -> usb -> dev , "PS:Power Saving Mode Enable...\n" ) ; } 