static void ccdc_apply_controls ( struct isp_ccdc_device * ccdc ) { if ( ccdc -> update & OMAP3ISP_CCDC_ALAW ) { ccdc_configure_alaw ( ccdc ) ; ccdc -> update &= ~ OMAP3ISP_CCDC_ALAW ; } if ( ccdc -> update & OMAP3ISP_CCDC_LPF ) { ccdc -> update &= ~ OMAP3ISP_CCDC_LPF ; } if ( ccdc -> update & OMAP3ISP_CCDC_BLCLAMP ) { ccdc_configure_clamp ( ccdc ) ; ccdc -> update &= ~ OMAP3ISP_CCDC_BLCLAMP ; } if ( ccdc -> update & OMAP3ISP_CCDC_BCOMP ) { ccdc_configure_black_comp ( ccdc ) ; ccdc -> update &= ~ OMAP3ISP_CCDC_BCOMP ; } } 