static inline int get_operstate ( char * operstate ) { if ( ! strcmp ( operstate , "up" ) ) { return NETDEV_OPERSTATE_UP ; } if ( ! strcmp ( operstate , "down" ) ) { return NETDEV_OPERSTATE_DOWN ; } if ( ! strcmp ( operstate , "notpresent" ) ) { return NETDEV_OPERSTATE_NOTPRESENT ; } if ( ! strcmp ( operstate , "lowerlayerdown" ) ) { return NETDEV_OPERSTATE_LOWERLAYERDOWN ; } if ( ! strcmp ( operstate , "dormant" ) ) { return NETDEV_OPERSTATE_DORMANT ; } return NETDEV_OPERSTATE_UNKNOWN ; } 