void axppmic_update_sensors ( void * arg ) { struct axppmic_softc * sc = arg ; int i ; for ( i = 0 ; sc -> sc_sensdata [ i ] . name ; i ++ ) { switch ( sc -> sc_sensdata [ i ] . type ) { case SENSOR_INDICATOR : axppmic_update_indicator ( sc , i ) ; break ; case SENSOR_PERCENT : axppmic_update_percent ( sc , i ) ; break ; case SENSOR_AMPHOUR : axppmic_update_amphour ( sc , i ) ; break ; default : break ; } } } 