static void CHaddrange ( struct parse * p , cset * cs , wint_t min , wint_t max ) { crange * newranges ; for ( ; min < NC && min <= max ; min ++ ) { CHadd ( p , cs , min ) ; } newranges = realloc ( cs -> ranges , ( cs -> nranges + 1 ) * sizeof ( * cs -> ranges ) ) ; if ( newranges == NULL ) { SETERROR ( REG_ESPACE ) ; return ; } cs -> ranges = newranges ; cs -> ranges [ cs -> nranges ] . min = min ; cs -> ranges [ cs -> nranges ] . max = max ; cs -> nranges ++ ; } 