static void dpu_hw_clear_errors ( struct dpu_hw_vbif * vbif , u32 * pnd_errors , u32 * src_errors ) { struct dpu_hw_blk_reg_map * c ; u32 pnd , src ; c = & vbif -> hw ; pnd = DPU_REG_READ ( c , VBIF_XIN_PND_ERR ) ; src = DPU_REG_READ ( c , VBIF_XIN_SRC_ERR ) ; if ( pnd_errors ) { * pnd_errors = pnd ; } if ( src_errors ) { * src_errors = src ; } DPU_REG_WRITE ( c , VBIF_XIN_CLR_ERR , pnd | src ) ; } 