static void start_next_payload_chain ( struct pbs_out * outs , struct_desc * sd , field_desc * fp , const uint8_t * inp , uint8_t * cur ) { dbg ( "next payload chain: saving message location '%s'.'%s'" , sd -> name , fp -> name ) ; outs -> next_payload_chain . loc = cur ; outs -> next_payload_chain . sd = sd ; outs -> next_payload_chain . fp = fp ; uint8_t n = * inp ; if ( n != ISAKMP_NEXT_NONE ) { esb_buf npb ; llog_pexpect ( outs -> outs_logger , HERE , "next payload chain: ignoring supplied '%s'.'%s' value %d:%s" , sd -> name , fp -> name , n , enum_show ( fp -> desc , n , & npb ) ) ; n = ISAKMP_NEXT_NONE ; } * cur = n ; } 