MODULE_LICENSE ( "GPL" ) ; MODULE_AUTHOR ( "Netfilter Core Team<coreteam@netfilter.org>" ) ; MODULE_DESCRIPTION ( "Xtables: multiple port matching for TCP, UDP, UDP-Lite, SCTP and DCCP" ) ; MODULE_ALIAS ( "ipt_multiport" ) ; MODULE_ALIAS ( "ip6t_multiport" ) ; static inline bool ports_match_v1 ( const struct xt_multiport_v1 * minfo , u_int16_t src , u_int16_t dst ) { unsigned int i ; u_int16_t s , e ; for ( i = 0 ; i < minfo -> count ; i ++ ) { s = minfo -> ports [ i ] ; if ( minfo -> pflags [ i ] ) { pr_debug ( "src or dst matches with %d-%d?\n" , s , e ) ; switch ( minfo -> flags ) { case XT_MULTIPORT_SOURCE : if ( src >= s && src <= e ) { return true ^ minfo -> invert ; } break ; case XT_MULTIPORT_DESTINATION : if ( dst >= s && dst <= e ) { return true ^ minfo -> invert ; } break ; case XT_MULTIPORT_EITHER : if ( ( dst >= s && dst <= e ) || ( src >= s && src <= e ) ) { return true ^ minfo -> invert ; } break ; default : break ; } } else { pr_debug ( "src or dst matches with %d?\n" , s ) ; switch ( minfo -> flags ) { case XT_MULTIPORT_SOURCE : if ( src == s ) { return true ^ minfo -> invert ; } break ; case XT_MULTIPORT_DESTINATION : if ( dst == s ) { return true ^ minfo -> invert ; } break ; case XT_MULTIPORT_EITHER : if ( src == s || dst == s ) { return true ^ minfo -> invert ; } break ; default : break ; } } } return minfo -> invert ; } 