static void test_ctl_write_default ( struct ctl_data * ctl ) { int err ; if ( snd_ctl_elem_info_is_inactive ( ctl -> info ) ) { ksft_print_msg ( "%s is inactive\n" , ctl -> name ) ; ksft_test_result_skip ( "write_default.%d.%d\n" , ctl -> card -> card , ctl -> elem ) ; return ; } if ( ! snd_ctl_elem_info_is_writable ( ctl -> info ) ) { ksft_print_msg ( "%s is not writeable\n" , ctl -> name ) ; ksft_test_result_skip ( "write_default.%d.%d\n" , ctl -> card -> card , ctl -> elem ) ; return ; } if ( ! snd_ctl_elem_info_is_readable ( ctl -> info ) ) { ksft_print_msg ( "%s couldn't read default\n" , ctl -> name ) ; ksft_test_result_skip ( "write_default.%d.%d\n" , ctl -> card -> card , ctl -> elem ) ; return ; } ksft_test_result ( err >= 0 , "write_default.%d.%d\n" , ctl -> card -> card , ctl -> elem ) ; } 