static void gxv_mort_feature_validate ( GXV_mort_feature f , GXV_Validator gxvalid ) { if ( f -> featureType >= gxv_feat_registry_length ) { GXV_TRACE ( ( "featureType %d is out of registered range, " "setting %d is unchecked\n" , f -> featureType , f -> featureSetting ) ) ; GXV_SET_ERR_IF_PARANOID ( FT_INVALID_DATA ) ; } if ( ! gxv_feat_registry [ f -> featureType ] . existence ) { GXV_TRACE ( ( "featureType %d is within registered area " "but undefined, setting %d is unchecked\n" , f -> featureType , f -> featureSetting ) ) ; GXV_SET_ERR_IF_PARANOID ( FT_INVALID_DATA ) ; } else { FT_Byte nSettings_max ; nSettings_max = gxv_feat_registry [ f -> featureType ] . nSettings ; if ( gxv_feat_registry [ f -> featureType ] . exclusive ) { nSettings_max = ( FT_Byte ) ( 2 * nSettings_max ) ; } GXV_TRACE ( ( "featureType %d is registered" , f -> featureType ) ) ; GXV_TRACE ( ( "setting %d" , f -> featureSetting ) ) ; GXV_TRACE ( ( "\n" ) ) ; } } 