static void usage ( void ) { _cupsLangPuts ( stdout , _ ( "Usage: lpinfo [options] -m\n" "       lpinfo [options] -v" ) ) ; _cupsLangPuts ( stdout , _ ( "Options:" ) ) ; _cupsLangPuts ( stdout , _ ( "-E                      Encrypt the connection to the server" ) ) ; _cupsLangPuts ( stdout , _ ( "-h server[:port]        Connect to the named server and port" ) ) ; _cupsLangPuts ( stdout , _ ( "-l                      Show verbose (long) output" ) ) ; _cupsLangPuts ( stdout , _ ( "-m                      Show models" ) ) ; _cupsLangPuts ( stdout , _ ( "-U username             Specify the username to use for authentication" ) ) ; _cupsLangPuts ( stdout , _ ( "-v                      Show devices" ) ) ; _cupsLangPuts ( stdout , _ ( "--device-id device-id   Show models matching the given IEEE 1284 device ID" ) ) ; _cupsLangPuts ( stdout , _ ( "--exclude-schemes scheme-list\n" "                        Exclude the specified URI schemes" ) ) ; _cupsLangPuts ( stdout , _ ( "--include-schemes scheme-list\n" "                        Include only the specified URI schemes" ) ) ; _cupsLangPuts ( stdout , _ ( "--language locale       Show models matching the given locale" ) ) ; _cupsLangPuts ( stdout , _ ( "--make-and-model name   Show models matching the given make and model name" ) ) ; _cupsLangPuts ( stdout , _ ( "--product name          Show models matching the given PostScript product" ) ) ; _cupsLangPuts ( stdout , _ ( "--timeout seconds       Specify the maximum number of seconds to discover devices" ) ) ; } 