static double get_rdelta ( AudioLimiterContext * s , double release , int sample_rate , double peak , double limit , double patt , int asc ) { double rdelta = ( 1.0 - patt ) / ( sample_rate * release ) ; if ( asc && s -> auto_release && s -> asc_c > 0 ) { double a_att = limit / ( s -> asc_coeff * s -> asc ) * ( double ) s -> asc_c ; if ( a_att > patt ) { if ( delta < rdelta ) { rdelta = delta ; } } } return rdelta ; } 