static void mt2712_delay_stage2ps ( struct mediatek_dwmac_plat_data * plat ) { struct mac_delay_struct * mac_delay = & plat -> mac_delay ; switch ( plat -> phy_mode ) { case PHY_INTERFACE_MODE_MII : case PHY_INTERFACE_MODE_RMII : mac_delay -> tx_delay *= 550 ; mac_delay -> rx_delay *= 550 ; break ; case PHY_INTERFACE_MODE_RGMII : case PHY_INTERFACE_MODE_RGMII_TXID : case PHY_INTERFACE_MODE_RGMII_RXID : case PHY_INTERFACE_MODE_RGMII_ID : mac_delay -> tx_delay *= 170 ; mac_delay -> rx_delay *= 170 ; break ; default : break ; } } 