static void ktd2692_expresswire_set_bit ( struct ktd2692_context * led , bool bit ) { if ( bit ) { udelay ( KTD2692_TIME_SHORT_BITSET_US ) ; gpiod_direction_output ( led -> ctrl_gpio , KTD2692_HIGH ) ; udelay ( KTD2692_TIME_LONG_BITSET_US ) ; } else { gpiod_direction_output ( led -> ctrl_gpio , KTD2692_LOW ) ; udelay ( KTD2692_TIME_LONG_BITSET_US ) ; gpiod_direction_output ( led -> ctrl_gpio , KTD2692_HIGH ) ; udelay ( KTD2692_TIME_SHORT_BITSET_US ) ; } } 