void ktest_make_sample_safe ( krb5_safe * s ) { ktest_make_sample_data ( & s -> user_data ) ; s -> timestamp = SAMPLE_TIME ; s -> usec = SAMPLE_USEC ; s -> seq_number = SAMPLE_SEQ_NUMBER ; s -> s_address = ealloc ( sizeof ( krb5_address ) ) ; ktest_make_sample_address ( s -> s_address ) ; s -> r_address = ealloc ( sizeof ( krb5_address ) ) ; ktest_make_sample_address ( s -> r_address ) ; ktest_make_sample_checksum ( s -> checksum ) ; } 