static void mtk_hdmi_hw_aud_set_i2s_fmt ( struct mtk_hdmi * hdmi , enum hdmi_aud_i2s_fmt i2s_fmt ) { u32 val ; val = mtk_hdmi_read ( hdmi , GRL_CFG0 ) ; switch ( i2s_fmt ) { case HDMI_I2S_MODE_RJT_24BIT : val |= CFG0_I2S_MODE_RTJ | CFG0_W_LENGTH_24BIT ; break ; case HDMI_I2S_MODE_RJT_16BIT : val |= CFG0_I2S_MODE_RTJ | CFG0_W_LENGTH_16BIT ; break ; case HDMI_I2S_MODE_LJT_24BIT : default : val |= CFG0_I2S_MODE_LTJ | CFG0_W_LENGTH_24BIT ; break ; case HDMI_I2S_MODE_LJT_16BIT : val |= CFG0_I2S_MODE_LTJ | CFG0_W_LENGTH_16BIT ; break ; case HDMI_I2S_MODE_I2S_24BIT : val |= CFG0_I2S_MODE_I2S | CFG0_W_LENGTH_24BIT ; break ; case HDMI_I2S_MODE_I2S_16BIT : val |= CFG0_I2S_MODE_I2S | CFG0_W_LENGTH_16BIT ; break ; } mtk_hdmi_write ( hdmi , GRL_CFG0 , val ) ; } 