static void dr_make_comp_nodes ( dr_board_t * bp ) { int i ; for ( i = 0 ; i < MAX_MEM_UNITS_PER_BOARD ; i ++ ) { dr_mem_unit_t * mp ; mp = dr_get_mem_unit ( bp , i ) ; dr_init_mem_unit ( mp ) ; } for ( i = 0 ; i < MAX_CPU_UNITS_PER_BOARD ; i ++ ) { dr_cpu_unit_t * cp ; cp = dr_get_cpu_unit ( bp , i ) ; dr_init_cpu_unit ( cp ) ; } for ( i = 0 ; i < MAX_IO_UNITS_PER_BOARD ; i ++ ) { dr_io_unit_t * ip ; ip = dr_get_io_unit ( bp , i ) ; dr_init_io_unit ( ip ) ; } dr_board_transition ( bp , DR_STATE_CONNECTED ) ; bp -> b_rstate = SBD_STAT_CONNECTED ; bp -> b_ostate = SBD_STAT_UNCONFIGURED ; ( void ) drv_getparm ( TIME , ( void * ) & bp -> b_time ) ; } 