void nft_bitwise_eval ( const struct nft_expr * expr , struct nft_regs * regs , const struct nft_pktinfo * pkt ) { const u32 * src = & regs -> data [ priv -> sreg ] ; u32 * dst = & regs -> data [ priv -> dreg ] ; switch ( priv -> op ) { case NFT_BITWISE_BOOL : nft_bitwise_eval_bool ( dst , src , priv ) ; break ; case NFT_BITWISE_LSHIFT : nft_bitwise_eval_lshift ( dst , src , priv ) ; break ; case NFT_BITWISE_RSHIFT : nft_bitwise_eval_rshift ( dst , src , priv ) ; break ; } } 