static int uvc_buffer_prepare ( struct vb2_buffer * vb ) { struct vb2_v4l2_buffer * vbuf = to_vb2_v4l2_buffer ( vb ) ; struct uvc_video_queue * queue = vb2_get_drv_priv ( vb -> vb2_queue ) ; struct uvc_buffer * buf = uvc_vbuf_to_buffer ( vbuf ) ; if ( vb -> type == V4L2_BUF_TYPE_VIDEO_OUTPUT && vb2_get_plane_payload ( vb , 0 ) > vb2_plane_size ( vb , 0 ) ) { uvc_dbg ( uvc_queue_to_stream ( queue ) -> dev , CAPTURE , "[E] Bytes used out of bounds\n" ) ; return - EINVAL ; } if ( unlikely ( queue -> flags & UVC_QUEUE_DISCONNECTED ) ) { return - ENODEV ; } buf -> error = 0 ; buf -> mem = vb2_plane_vaddr ( vb , 0 ) ; buf -> length = vb2_plane_size ( vb , 0 ) ; if ( vb -> type != V4L2_BUF_TYPE_VIDEO_OUTPUT ) { buf -> bytesused = 0 ; } else { buf -> bytesused = vb2_get_plane_payload ( vb , 0 ) ; } return 0 ; } 