static void iwl_mvm_scan_lmac_dwell ( struct iwl_mvm * mvm , struct iwl_scan_req_lmac * cmd , struct iwl_mvm_scan_params * params ) { cmd -> active_dwell = IWL_SCAN_DWELL_ACTIVE ; cmd -> passive_dwell = IWL_SCAN_DWELL_PASSIVE ; cmd -> fragmented_dwell = IWL_SCAN_DWELL_FRAGMENTED ; cmd -> extended_dwell = IWL_SCAN_DWELL_EXTENDED ; cmd -> max_out_time = cpu_to_le32 ( scan_timing [ params -> type ] . max_out_time ) ; cmd -> suspend_time = cpu_to_le32 ( scan_timing [ params -> type ] . suspend_time ) ; } 