void test_url_scp__ipv4_user_port ( void ) { cl_git_pass ( git_net_url_parse_scp ( & conndata , "[git@192.168.99.88:1111]:/resource/a/b/c" ) ) ; cl_assert_equal_s ( conndata . scheme , "ssh" ) ; cl_assert_equal_s ( conndata . host , "192.168.99.88" ) ; cl_assert_equal_s ( conndata . port , "1111" ) ; cl_assert_equal_s ( conndata . path , "/resource/a/b/c" ) ; cl_assert_equal_s ( conndata . username , "git" ) ; cl_assert_equal_p ( conndata . password , NULL ) ; } 