static XidBoundsViolation check_mxid_in_range ( MultiXactId mxid , HeapCheckContext * ctx ) { if ( MultiXactIdPrecedes ( mxid , ctx -> relminmxid ) ) { return XID_PRECEDES_RELMIN ; } if ( MultiXactIdPrecedes ( mxid , ctx -> oldest_mxact ) ) { return XID_PRECEDES_CLUSTERMIN ; } if ( MultiXactIdPrecedesOrEquals ( ctx -> next_mxact , mxid ) ) { return XID_IN_FUTURE ; } return XID_BOUNDS_OK ; } 