static int fusb302_handle_togdone_snk ( struct fusb302_chip * chip , u8 togdone_result ) { int ret = 0 ; u8 status0 ; u8 bc_lvl ; enum typec_cc_polarity cc_polarity ; enum typec_cc_status cc_status_active , cc1 , cc2 ; ret = fusb302_set_cc_pull ( chip , false , true ) ; if ( ret < 0 ) { fusb302_log ( chip , "cannot set cc to pull down, ret=%d" , ret ) ; return ret ; } cc_polarity = ( togdone_result == FUSB_REG_STATUS1A_TOGSS_SNK1 ) ?TYPEC_POLARITY_CC1 : TYPEC_POLARITY_CC2 ; ret = fusb302_set_cc_polarity ( chip , cc_polarity ) ; if ( ret < 0 ) { fusb302_log ( chip , "cannot set cc polarity %s, ret=%d" , cc_polarity_name [ cc_polarity ] , ret ) ; return ret ; } ret = fusb302_i2c_read ( chip , FUSB_REG_STATUS0 , & status0 ) ; if ( ret < 0 ) { return ret ; } bc_lvl = status0 & FUSB_REG_STATUS0_BC_LVL_MASK ; cc_status_active = fusb302_bc_lvl_to_cc ( bc_lvl ) ; if ( cc_status_active == TYPEC_CC_OPEN ) { fusb302_log ( chip , "restart toggling as CC_OPEN detected" ) ; ret = fusb302_set_toggling ( chip , chip -> toggling_mode ) ; return ret ; } cc1 = ( cc_polarity == TYPEC_POLARITY_CC1 ) ?cc_status_active : TYPEC_CC_OPEN ; cc2 = ( cc_polarity == TYPEC_POLARITY_CC2 ) ?cc_status_active : TYPEC_CC_OPEN ; if ( ( chip -> cc1 != cc1 ) || ( chip -> cc2 != cc2 ) ) { chip -> cc1 = cc1 ; chip -> cc2 = cc2 ; tcpm_cc_change ( chip -> tcpm_port ) ; } ret = fusb302_set_toggling ( chip , TOGGLINE_MODE_OFF ) ; if ( ret < 0 ) { fusb302_log ( chip , "cannot set toggling mode off, ret=%d" , ret ) ; return ret ; } ret = fusb302_i2c_clear_bits ( chip , FUSB_REG_MASK , FUSB_REG_MASK_BC_LVL ) ; chip -> intr_bc_lvl = true ; fusb302_log ( chip , "detected cc1=%s, cc2=%s" , typec_cc_status_name [ cc1 ] , typec_cc_status_name [ cc2 ] ) ; return ret ; } 