static void i2c_acpi_register_devices ( struct i2c_adapter * adap ) { acpi_status status ; status = acpi_walk_namespace ( ACPI_TYPE_DEVICE , ACPI_ROOT_OBJECT , I2C_ACPI_MAX_SCAN_DEPTH , i2c_acpi_add_device , NULL , adap , NULL ) ; if ( ACPI_FAILURE ( status ) ) { dev_warn ( & adap -> dev , "failed to enumerate I2C slaves\n" ) ; } } 