extern char * trigger_type ( uint32_t trig_type ) { if ( trig_type == TRIGGER_TYPE_UP ) { return "up" ; } if ( trig_type == TRIGGER_TYPE_DOWN ) { return "down" ; } if ( trig_type == TRIGGER_TYPE_DRAINED ) { return "drained" ; } if ( trig_type == TRIGGER_TYPE_FAIL ) { return "fail" ; } if ( trig_type == TRIGGER_TYPE_IDLE ) { return "idle" ; } if ( trig_type == TRIGGER_TYPE_TIME ) { return "time" ; } if ( trig_type == TRIGGER_TYPE_FINI ) { return "fini" ; } if ( trig_type == TRIGGER_TYPE_RECONFIG ) { return "reconfig" ; } if ( trig_type == TRIGGER_TYPE_PRI_CTLD_FAIL ) { return "primary_slurmctld_failure" ; } if ( trig_type == TRIGGER_TYPE_PRI_CTLD_RES_OP ) { return "primary_slurmctld_resumed_operation" ; } if ( trig_type == TRIGGER_TYPE_PRI_CTLD_RES_CTRL ) { return "primary_slurmctld_resumed_control" ; } if ( trig_type == TRIGGER_TYPE_PRI_CTLD_ACCT_FULL ) { return "primary_slurmctld_acct_buffer_full" ; } if ( trig_type == TRIGGER_TYPE_BU_CTLD_FAIL ) { return "backup_slurmctld_failure" ; } if ( trig_type == TRIGGER_TYPE_BU_CTLD_RES_OP ) { return "backup_slurmctld_resumed_operation" ; } if ( trig_type == TRIGGER_TYPE_BU_CTLD_AS_CTRL ) { return "backup_slurmctld_assumed_control" ; } if ( trig_type == TRIGGER_TYPE_PRI_DBD_FAIL ) { return "primary_slurmdbd_failure" ; } if ( trig_type == TRIGGER_TYPE_PRI_DBD_RES_OP ) { return "primary_slurmdbd_resumed_operation" ; } if ( trig_type == TRIGGER_TYPE_PRI_DB_FAIL ) { return "primary_database_failure" ; } if ( trig_type == TRIGGER_TYPE_PRI_DB_RES_OP ) { return "primary_database_resumed_operation" ; } if ( trig_type == TRIGGER_TYPE_BURST_BUFFER ) { return "burst_buffer" ; } if ( trig_type == TRIGGER_TYPE_DRAINING ) { return "draining" ; } else { return "unknown" ; } } 