static void print_current_user ( void ) { gchar * cur_user , * cur_group ; if ( started_with_special_privs ( ) ) { cur_user = get_cur_username ( ) ; cur_group = get_cur_groupname ( ) ; fprintf ( stderr , "Running as user \"%s\" and group \"%s\"." , cur_user , cur_group ) ; g_free ( cur_group ) ; if ( running_with_special_privs ( ) ) { fprintf ( stderr , " This could be dangerous." ) ; } fprintf ( stderr , "\n" ) ; } } 