static qboolean SV_CheckPaused ( void ) { int count ; client_t * cl ; int i ; count = 0 ; for ( i = 0 , cl = svs . clients ; i < sv_maxclients -> integer ; i ++ , cl ++ ) { if ( cl -> state >= CS_CONNECTED && cl -> netchan . remoteAddress . type != NA_BOT ) { count ++ ; } } if ( count > 1 ) { sv_paused -> integer = 0 ; return qfalse ; } sv_paused -> integer = 1 ; return qtrue ; } 