static void show_summary ( struct ctl_sum * sum ) { printf ( "Router ID: %s\n" , inet_ntoa ( sum -> rtr_id ) ) ; printf ( "Uptime: %s\n" , fmt_timeframe_core ( sum -> uptime ) ) ; printf ( "RFC1583 compatibility flag is " ) ; if ( sum -> rfc1583compat ) { printf ( "enabled\n" ) ; } else { printf ( "disabled\n" ) ; } printf ( "SPF delay is %d msec(s), hold time between two SPFs " "is %d msec(s)\n" , sum -> spf_delay , sum -> spf_hold_time ) ; printf ( "Number of external LSA(s) %d (Checksum sum 0x%x)\n" , sum -> num_ext_lsa , sum -> ext_lsa_cksum ) ; } 