int eibnx_fip_solicit_mcast ( eibnx_thr_info_t * info ) { eibnx_wqe_t * swqe ; int ret ; if ( ( swqe = eibnx_acquire_swqe ( info , KM_SLEEP ) ) == NULL ) { return ( ENX_E_FAILURE ) ; } ret = eibnx_fip_make_solicit_pkt ( info , swqe ) ; if ( ret != ENX_E_SUCCESS ) { eibnx_release_swqe ( swqe ) ; return ( ENX_E_FAILURE ) ; } ret = eibnx_fip_send_solicit_pkt ( info , swqe , NULL ) ; if ( ret != ENX_E_SUCCESS ) { eibnx_release_swqe ( swqe ) ; return ( ENX_E_FAILURE ) ; } return ( ENX_E_SUCCESS ) ; } 