char * yperr_string ( int code ) { switch ( code ) { case 0 : return ( "yp operation succeeded" ) ; case YPERR_BADARGS : return ( "args to yp function are bad" ) ; case YPERR_RPC : return ( "RPC failure on yp operation" ) ; case YPERR_DOMAIN : return ( "can't bind to a server which serves domain" ) ; case YPERR_MAP : return ( "no such map in server's domain" ) ; case YPERR_KEY : return ( "no such key in map" ) ; case YPERR_YPERR : return ( "internal yp server or client error" ) ; case YPERR_RESRC : return ( "local resource allocation failure" ) ; case YPERR_NOMORE : return ( "no more records in map database" ) ; case YPERR_PMAP : return ( "can't communicate with rpcbind" ) ; case YPERR_YPBIND : return ( "can't communicate with ypbind" ) ; case YPERR_YPSERV : return ( "can't communicate with ypserv" ) ; case YPERR_NODOM : return ( "local domain name not set" ) ; case YPERR_BADDB : return ( "yp map data base is bad" ) ; case YPERR_VERS : return ( "yp client/server version mismatch" ) ; case YPERR_ACCESS : return ( "permission denied" ) ; case YPERR_BUSY : return ( "database is busy" ) ; } return ( "unknown yp client error code" ) ; } 