static int sony_nc_hotkeys_decode ( u32 event , unsigned int handle ) { int ret = - EINVAL ; unsigned int result ; struct sony_nc_event * key_event ; if ( sony_call_snc_handle ( handle , 0x200 , & result ) ) { dprintk ( "Unable to decode event 0x%.2x 0x%.2x\n" , handle , event ) ; return - EINVAL ; } result &= 0xFF ; if ( handle == 0x0100 ) { key_event = sony_100_events ; } else { key_event = sony_127_events ; } for ( ; key_event -> data ; key_event ++ ) { if ( key_event -> data == result ) { ret = key_event -> event ; break ; } } if ( ! key_event -> data ) { pr_info ( "Unknown hotkey 0x%.2x/0x%.2x (handle 0x%.2x)\n" , event , result , handle ) ; } return ret ; } 