extern void scontrol_print_node ( char * node_name , node_info_msg_t * node_buffer_ptr ) { int i , j , print_cnt = 0 ; static int last_inx = 0 ; for ( j = 0 ; j < node_buffer_ptr -> record_count ; j ++ ) { if ( node_name ) { i = ( j + last_inx ) % node_buffer_ptr -> record_count ; if ( ( node_buffer_ptr -> node_array [ i ] . name == NULL ) || xstrcmp ( node_name , node_buffer_ptr -> node_array [ i ] . name ) ) { continue ; } } if ( node_buffer_ptr -> node_array [ j ] . name == NULL ) { continue ; } else { i = j ; } print_cnt ++ ; slurm_print_node_table ( stdout , & node_buffer_ptr -> node_array [ i ] , one_liner ) ; if ( node_name ) { last_inx = i ; break ; } } if ( print_cnt == 0 ) { if ( node_name ) { if ( quiet_flag != 1 ) { printf ( "Node %s not found\n" , node_name ) ; } } if ( quiet_flag != 1 ) { printf ( "No nodes in the system\n" ) ; } } } 