static int emac_check_deps ( struct emac_instance * dev , struct emac_depentry * deps ) { int i , there = 0 ; struct device_node * np ; for ( i = 0 ; i < EMAC_DEP_COUNT ; i ++ ) { if ( deps [ i ] . phandle == 0 ) { continue ; } if ( i == EMAC_DEP_PREV_IDX ) { np = * ( dev -> blist - 1 ) ; if ( np == NULL ) { deps [ i ] . phandle = 0 ; there ++ ; continue ; } if ( deps [ i ] . node == NULL ) { deps [ i ] . node = of_node_get ( np ) ; } } if ( deps [ i ] . node == NULL ) { deps [ i ] . node = of_find_node_by_phandle ( deps [ i ] . phandle ) ; } if ( deps [ i ] . node == NULL ) { continue ; } if ( deps [ i ] . ofdev == NULL ) { deps [ i ] . ofdev = of_find_device_by_node ( deps [ i ] . node ) ; } if ( deps [ i ] . ofdev == NULL ) { continue ; } if ( deps [ i ] . drvdata == NULL ) { deps [ i ] . drvdata = platform_get_drvdata ( deps [ i ] . ofdev ) ; } if ( deps [ i ] . drvdata != NULL ) { there ++ ; } } return there == EMAC_DEP_COUNT ; } 