l_ok numaWriteStderr ( NUMA * na ) { l_int32 i , n ; l_float32 startx , delx ; if ( ! na ) { return ERROR_INT ( "na not defined" , __func__ , 1 ) ; } n = numaGetCount ( na ) ; lept_stderr ( "\nNuma Version %d\n" , NUMA_VERSION_NUMBER ) ; lept_stderr ( "Number of numbers = %d\n" , n ) ; for ( i = 0 ; i < n ; i ++ ) { lept_stderr ( "  [%d] = %f\n" , i , na -> array [ i ] ) ; } lept_stderr ( "\n" ) ; numaGetParameters ( na , & startx , & delx ) ; return 0 ; } 