static int qed_slowpath_vf_setup_int ( struct qed_dev * cdev ) { int rc ; memset ( & cdev -> int_params , 0 , sizeof ( qed_int_params ) ) ; cdev -> int_params . in . int_mode = QED_INT_MODE_MSIX ; qed_vf_get_num_rxqs ( QED_LEADING_HWFN ( cdev ) , & cdev -> int_params . in . num_vectors ) ; cdev -> int_params . in . min_msix_cnt = cdev -> num_hwfns ; rc = qed_set_int_mode ( cdev , true ) ; if ( rc ) { return rc ; } cdev -> int_params . fp_msix_base = 0 ; cdev -> int_params . fp_msix_cnt = cdev -> int_params . out . num_vectors ; return 0 ; } 