static void dpcd_reduce_address_range ( const uint32_t extended_address , uint8_t * const extended_data , const uint32_t extended_size , const uint32_t reduced_address , uint8_t * const reduced_data , const uint32_t reduced_size ) { const uint32_t offset = reduced_address - extended_address ; if ( extended_data == reduced_data ) { return ; } memcpy ( & extended_data [ offset ] , reduced_data , reduced_size ) ; } 