static void wdg_input_borders ( struct wdg_object * wo ) { WDG_WO_EXT ( wdg_input_handle , ww ) ; if ( wo -> flags & WDG_OBJ_FOCUSED ) { wattron ( ww -> win , A_BOLD ) ; wbkgdset ( ww -> win , COLOR_PAIR ( wo -> focus_color ) ) ; } else { wbkgdset ( ww -> win , COLOR_PAIR ( wo -> border_color ) ) ; } box ( ww -> win , 0 , 0 ) ; wbkgdset ( ww -> win , COLOR_PAIR ( wo -> title_color ) ) ; if ( wo -> title ) { switch ( wo -> align ) { case WDG_ALIGN_LEFT : wmove ( ww -> win , 0 , 3 ) ; break ; case WDG_ALIGN_CENTER : wmove ( ww -> win , 0 , ( c - strlen ( wo -> title ) ) / 2 ) ; break ; case WDG_ALIGN_RIGHT : wmove ( ww -> win , 0 , c - strlen ( wo -> title ) - 3 ) ; break ; } wprintw ( ww -> win , "%s" , wo -> title ) ; } if ( wo -> flags & WDG_OBJ_FOCUSED ) { wattroff ( ww -> win , A_BOLD ) ; } } 