static void nd_dax_release ( struct device * dev ) { struct nd_region * nd_region = to_nd_region ( dev -> parent ) ; struct nd_dax * nd_dax = to_nd_dax ( dev ) ; struct nd_pfn * nd_pfn = & nd_dax -> nd_pfn ; dev_dbg ( dev , "trace\n" ) ; nd_detach_ndns ( dev , & nd_pfn -> ndns ) ; ida_simple_remove ( & nd_region -> dax_ida , nd_pfn -> id ) ; kfree ( nd_dax ) ; } 