M_http_method_t M_http_method_from_str ( const char * method ) { if ( M_str_caseeq ( method , "OPTIONS" ) ) { return M_HTTP_METHOD_OPTIONS ; } if ( M_str_caseeq ( method , "GET" ) ) { return M_HTTP_METHOD_GET ; } if ( M_str_caseeq ( method , "HEAD" ) ) { return M_HTTP_METHOD_HEAD ; } if ( M_str_caseeq ( method , "POST" ) ) { return M_HTTP_METHOD_POST ; } if ( M_str_caseeq ( method , "PUT" ) ) { return M_HTTP_METHOD_PUT ; } if ( M_str_caseeq ( method , "DELETE" ) ) { return M_HTTP_METHOD_DELETE ; } if ( M_str_caseeq ( method , "CONNECT" ) ) { return M_HTTP_METHOD_CONNECT ; } if ( M_str_caseeq ( method , "PATCH" ) ) { return M_HTTP_METHOD_PATCH ; } return M_HTTP_METHOD_UNKNOWN ; } 