long radeon_drm_ioctl ( struct file * filp , unsigned int cmd , unsigned long arg ) { struct drm_file * file_priv = filp -> private_data ; struct drm_device * dev ; long ret ; dev = file_priv -> minor -> dev ; ret = pm_runtime_get_sync ( dev -> dev ) ; if ( ret < 0 ) { return ret ; } pm_runtime_mark_last_busy ( dev -> dev ) ; pm_runtime_put_autosuspend ( dev -> dev ) ; return ret ; } 