void acpi_ex_unlink_mutex ( union acpi_operand_object * obj_desc ) { struct acpi_thread_state * thread = obj_desc -> mutex . owner_thread ; if ( ! thread ) { return ; } if ( obj_desc -> mutex . prev ) { ( obj_desc -> mutex . prev ) -> mutex . next = obj_desc -> mutex . next ; ( obj_desc -> mutex . prev ) -> mutex . original_sync_level = obj_desc -> mutex . original_sync_level ; } else { thread -> acquired_mutex_list = obj_desc -> mutex . next ; } } 