void editor_process_sdl_event ( SDL_Event * e ) { switch ( e -> type ) { case SDL_MOUSEMOTION : case SDL_MOUSEBUTTONDOWN : case SDL_MOUSEBUTTONUP : case SDL_MOUSEWHEEL : break ; case SDL_KEYDOWN : if ( in_editor ) { switch ( e -> key . keysym . sym ) { case SDLK_RIGHT : editor_key ( STBTE_scroll_right ) ; break ; case SDLK_LEFT : editor_key ( STBTE_scroll_left ) ; break ; case SDLK_UP : editor_key ( STBTE_scroll_up ) ; break ; case SDLK_DOWN : editor_key ( STBTE_scroll_down ) ; break ; } switch ( e -> key . keysym . scancode ) { case SDL_SCANCODE_S : editor_key ( STBTE_tool_select ) ; break ; case SDL_SCANCODE_B : editor_key ( STBTE_tool_brush ) ; break ; case SDL_SCANCODE_E : editor_key ( STBTE_tool_erase ) ; break ; case SDL_SCANCODE_R : editor_key ( STBTE_tool_rectangle ) ; break ; case SDL_SCANCODE_I : editor_key ( STBTE_tool_eyedropper ) ; break ; case SDL_SCANCODE_L : editor_key ( STBTE_tool_link ) ; break ; case SDL_SCANCODE_G : editor_key ( STBTE_act_toggle_grid ) ; break ; } if ( ( e -> key . keysym . mod & KMOD_CTRL ) && ! ( e -> key . keysym . mod & ~ KMOD_CTRL ) ) { switch ( e -> key . keysym . scancode ) { case SDL_SCANCODE_X : editor_key ( STBTE_act_cut ) ; break ; case SDL_SCANCODE_C : editor_key ( STBTE_act_copy ) ; break ; case SDL_SCANCODE_V : editor_key ( STBTE_act_paste ) ; break ; case SDL_SCANCODE_Z : editor_key ( STBTE_act_undo ) ; break ; case SDL_SCANCODE_Y : editor_key ( STBTE_act_redo ) ; break ; } } } break ; } } 