static void tuntap_init ( struct net_device * dev , void * data ) { struct uml_net_private * pri ; struct tuntap_data * tpri ; struct tuntap_init * init = data ; pri = netdev_priv ( dev ) ; tpri = ( tuntap_data * ) pri -> user ; tpri -> dev_name = init -> dev_name ; tpri -> fixed_config = ( init -> dev_name != NULL ) ; tpri -> gate_addr = init -> gate_addr ; tpri -> dev = dev ; printk ( KERN_INFO "TUN/TAP backend - " ) ; if ( tpri -> gate_addr != NULL ) { printk ( KERN_CONT "IP = %s" , tpri -> gate_addr ) ; } printk ( KERN_CONT "\n" ) ; } 